mapper.select:
SELECT BOARD_NO, FREE_TITLE, FREE_WRITER, FREE_COUNT, F_CREATE_DATE, '자유롭게' AS CATEGORY FROM FREE_TBL WHERE F_STATUS = 'Y' AND FREE_WRITER = #{memberNickname}
UNION ALL SELECT BOARD_NO, PARTY_TITLE, PARTY_WRITER, PARTY_COUNT, P_CREATE_DATE, '우리 함께' FROM PARTY_TBL WHERE P_STATUS = 'Y' AND PARTY_WRITER = #{memberNickname}
UNION ALL SELECT BOARD_NO, REVIEW_TITLE, REVIEW_WRITER, REVIEW_COUNT, R_CREATE_DATE, '여행 후기' FROM REVIEW_TBL WHERE R_STATUS = 'Y' AND REVIEW_WRITER = #{memberNickname} ORDER BY BOARD_NO DESC
